import './App.css';
import { useState } from 'react';
import { useEffect } from 'react';

function App() {
  const [Likes, setLikes] = useState(0);
  const [comment, setComment] = useState([]);
  useEffect(() => {
    if (Likes >= 20 && comment.length >= 3) {
      alert("You are famous Post")
    }
  }, [Likes, comment.length]);
  useEffect()
  return (
    <div className="App">
      <h1>Simple Effect</h1>
      < div className="post">
        <div>
          <h2>Very simple post</h2>
        </div>
        <div className='likes'>
          <h3>Number Like {Likes}</h3>
          <button onClick={() => { setLikes(Likes + 1) }}>Like!</button>
      </div>

          <form action="" className='forms' onSubmit={(e) => {
            setComment([...comment, e.currentTarget.comment.value]);
            e.preventDefault();
          }}>
          <h3>Comment {comment.length}</h3>


            <input type="text" name="comment" required />
            <button >Comment!</button>
          </form>



      </div>
    </div>
  );
}

export default App;
