import './App.css';
import { useState } from 'react';
import { useEffect } from 'react';

function App() {
    const [Likes, setLikes] = useState(0);
    const [comment, setComment] = useState([]);
    const [message, setMessage] = useState();
    const [loading, setLoading] = useState(false)
    useEffect(() => {
        if (Likes >= 20 && comment.length >= 3) {
            setMessage("You are famous Post");
        }
    }, [Likes, comment.length]);
    useEffect(() => {

        setLoading(true);
        setTimeout(() => {
            alert("Looding");
        }, 10000);
        setLoading(false)
        return () => {
            log
        }

    }, [])
    return (

        <div className="App">
            <h1>Simple Effect</h1>
            < div className="post">
                <div>
                    <h2>Very simple post</h2>
                </div>
                <div className='likes'>
                    <h3>Number Like {Likes}</h3>
                    <button onClick={() => { setLikes(Likes + 1) }}>Like!</button>
                </div>
                <div>
                    <form action="" onSubmit={(e) => {
                        setComment([...comment, e.currentTarget.comment.value]);
                        e.preventDefault();
                    }}>
                        <h3 >Comment {comment.length}</h3>
                        <input type="text" name="comment" required />
                        <button >Comment!</button>
                    </form>
                    <h3>{ message}</h3>
                </div>
            </div>
        </div>
    );
}

export default App;
