import React, { useState } from 'react'

function Cell() {
    const [cells, setCells] = useState(Array(9).fill(''));
    const [turn, setTurn] = useState("X");
    const cellClicked = (index) => {
        if (cells[index] !== "") return;
        const newCell = [...cells];
        newCell[index] = turn;
        setCells(newCell);
        setTurn(turn)
    }

    const newCells = [...cells];
    return (
        <div className='cells'>
            {cells.map((box, index) =>
                <div key={index}
                    className={`cell ${box}`}
                    onClick={() => { cellClicked(index) }}
                >
                    {box}
                </div>)}
        </div>
    )
}

export default Cell
