import './App.css';
import { useState } from 'react';
import { useEffect } from 'react';

function App() {
  const [Likes, setLikes] = useState(0);
  useEffect(()=>{},[Likes])
  return (
    <div className="App">
      <h1>Simple Effect</h1>
      <div className="post">
        <div>
          <h2>Very simple post</h2>
        </div>
        <div className='likes'>
          <h3>Number Like {Likes}</h3>
          <button onClick={() => { setLikes(Likes + 1) }}>Like!</button>
        </div>
      </div>
    </div>
  );
}

export default App;
